const modal=document.querySelector(".search__modal"),form=modal.querySelector(".search__form"),toggleModalButton=document.querySelector(".search__button-modal"),submitButton=modal.querySelector(".modal__button"),firstInput=form.querySelector("input"),arrival=form.querySelector(".input-big-arrival"),departure=form.querySelector(".input-big-departure"),adults=form.querySelector(".search__total-adults"),children=form.querySelector(".search__total-children");toggleModalButton.addEventListener("click",function(e){e.preventDefault(),firstInput.focus(),modal&&modal.classList.toggle("search__modal-close")}),submitButton.addEventListener("click",function(e){form.checkValidity()?(localStorage.setItem("arrival",arrival.value),localStorage.setItem("departure",departure.value),localStorage.setItem("adults",adults.value),localStorage.setItem("children",children.value)):(e.preventDefault(),form.classList.add("search__modal-error"))}),form.addEventListener("animationend",function(){form.classList.remove("search__modal-error")});
